mapper.select:
where 1 = 1
and blog.title like concat('%', #{title}, '%')
and type.id = #{typeId} and type.id = blog.type_id
and tag.id = #{tagId}
and blog.recommend = #{recommend}

mapper.insert:
insert into
    t_blog(
        appreciation, commentabled, content, create_time, description, first_picture, 
        flag, published, recommend, share_statement, title, update_time, views, type_id, user_id
        )
    values(
        #{appreciation}, #{commentabled}, #{content}, #{createTime}, #{description},
        #{firstPicture}, #{flag}, #{published}, #{recommend}, #{shareStatement}, 
        #{title}, #{updateTime}, #{views}, #{type.id}, #{user.id}
        )

mapper.update:
update 
    t_blog_tags
set
    tags_id = #{tagId}
where
    blogs_id = #{blogId}
